	/*
	* Observe function
	*/
	public static function mxvfos_wp_ajax()
	{

		add_action( 'wp_ajax_mxvfos_send_mail', array( 'MXVFOSSendMailTo', 'prepare_payment_sending' ), 10, 1 );

		add_action( 'wp_ajax_nopriv_mxvfos_send_mail', array( 'MXVFOSSendMailTo', 'prepare_payment_sending' ), 10, 1 );

	}

	/*
	* Prepare for data updates
	*/
	public static function prepare_payment_sending()
	{

		// Checked POST nonce is not empty
		if( empty( $_POST['nonce'] ) ) wp_die( '0' );

		// Checked or nonce match
		if( wp_verify_nonce( $_POST['nonce'], 'mxvfos_wpnonce_request' ) ) {

			$user_data = $_POST['user_data'];

			$form_data = $_POST['form_data'];
			
			// progress
			self::progress_payment_sending( $user_data, $form_data );			

		}

		wp_die();

	}

		// Send email
		public static function progress_payment_sending( $user_data, $form_data )
		{

			$dirname = __DIR__. '/pdf/';

			$file_name = time();

			$attachments = array( $dirname . $file_name . '.pdf' );

			self::create_pdf_file( $dirname, $file_name, $user_data, $form_data );

			$form_options = get_option( '_mx_visual_form_options' );

			$form_options = maybe_unserialize( $form_options );

			$customer_email = $form_options['mx-field-164'];

			$customer_email = str_replace( ' ', '', $customer_email );

			$array_of_emails = explode( ',', $customer_email );

			add_filter( 'wp_mail_content_type', array( 'MXVFOSSendMailTo', 'set_html_content_type' ) );			

			$message = '<h1>Hello Admin</h1><p>You have just received a new simulator configuration inquiry. Please find the attachment for details.</p>';

			$message .= self::mxvfos_get_message( $user_data, $form_data );

			$user_name = $user_data['user_name'] . ' ' . $user_data['user_last_name'];

			$user_email = 'no-reply@testing.foresightsports.eu';

			$headers = 'From: ' . $user_name . ' <' . $user_email . '>' . "\r\n";

			foreach ( $array_of_emails as $key => $value ) {
				
				wp_mail( $value, 'Simulation Configurator requset', $message, $headers, $attachments );

			}			

			echo 'sent';

			// remove filter
			remove_filter( 'wp_mail_content_type', array( 'MXVFOSSendMailTo', 'set_html_content_type' ) );


		}

